// src/components/Reservations/MyReservations.js
import React, { useState, useEffect } from 'react';
import api from '../../services/api';

const MyReservations = () => {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchReservations = async () => {
      try {
        const response = await api.get('reservations/');
        setReservations(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching reservations:', error);
        setLoading(false);
      }
    };

    fetchReservations();
  }, []);

  const handleCancelReservation = async (id) => {
    if (window.confirm('Are you sure you want to cancel this reservation?')) {
      try {
        await api.delete(`reservations/${id}/`);
        setReservations(reservations.filter(reservation => reservation.id !== id));
      } catch (error) {
        console.error('Error canceling reservation:', error);
        alert('Failed to cancel reservation. Please try again.');
      }
    }
  };

  if (loading) return <div className="text-center mt-5">Loading...</div>;

  return (
    <div
      style={{
        backgroundColor: '#001f3d', // Dark blue background
        minHeight: '100vh',
        color: '#fff',
        fontFamily: 'Segoe UI, sans-serif',
        padding: '80px 0',
        textAlign: 'center',
        backgroundAttachment: 'fixed',
      }}
    >
      {/* Dark Overlay */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.6)', // Subtle dark overlay to enhance readability
          zIndex: -1,
        }}
      ></div>

      {/* SportSched Branding */}
      <div className="container mb-5">
        <h1
          className="text-light fw-bold"
          style={{
            fontSize: '3rem',
            textShadow: '5px 5px 15px rgba(0,0,0,0.8)',
            fontFamily: 'Arial, sans-serif',
          }}
        >
          SportSched
        </h1>
      </div>

      {/* Main Title */}
      <div className="container mb-5">
        <h2 className="text-center text-light fw-bold" style={{ fontSize: '2.5rem', textShadow: '5px 5px 15px rgba(0,0,0,0.8)' }}>
          My Reservations
        </h2>
      </div>

      {/* Content */}
      {reservations.length === 0 ? (
        <div className="alert alert-info text-center" style={{ backgroundColor: 'rgba(255, 165, 0, 0.7)', borderRadius: '10px' }}>
          You have no reservations yet.
        </div>
      ) : (
        <div className="container table-responsive">
          <table
            className="table table-striped table-hover"
            style={{
              backgroundColor: 'rgba(255, 165, 0, 0.8)', // Peach-colored boxes
              backdropFilter: 'blur(10px)', // Slight blur for modern effect
              borderRadius: '15px',
              color: '#fff',
            }}
          >
            <thead>
              <tr>
                <th>Facility</th>
                <th>Date</th>
                <th>Time</th>
                <th>Booked On</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {reservations.map((reservation) => (
                <tr key={reservation.id}>
                  <td>{reservation.facility_name}</td>
                  <td>{new Date(reservation.time_slot_details.date).toLocaleDateString()}</td>
                  <td>
                    {new Date(`2000-01-01T${reservation.time_slot_details.start_time}`).toLocaleTimeString([], {
                      hour: '2-digit',
                      minute: '2-digit',
                    })}{' '}
                    -{' '}
                    {new Date(`2000-01-01T${reservation.time_slot_details.end_time}`).toLocaleTimeString([], {
                      hour: '2-digit',
                      minute: '2-digit',
                    })}
                  </td>
                  <td>{new Date(reservation.created_at).toLocaleDateString()}</td>
                  <td>
                    <button
                      className="btn btn-sm btn-danger"
                      onClick={() => handleCancelReservation(reservation.id)}
                    >
                      Cancel
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default MyReservations;
