// src/components/Common/Navbar.js
import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';

const Navbar = () => {
  const { isAuthenticated, logout, user } = useContext(AuthContext); // Get username here
  const navigate = useNavigate();

  const handleLogout = async () => {
    await logout();
    navigate('/login');
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark" style={navbarStyle}>
      <div className="container">
        <Link
          className="navbar-brand"
          to="/"
          style={brandStyle}
        >
          College Sports Reservation
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto">
            <li className="nav-item">
              <Link className="nav-link" to="/facilities" style={linkStyle}>
                Facilities
              </Link>
            </li>
            {isAuthenticated && (
              <li className="nav-item">
                <Link className="nav-link" to="/my-reservations" style={linkStyle}>
                  My Reservations
                </Link>
              </li>
            )}
          </ul>
          <div className="d-flex ms-auto">
            {isAuthenticated ? (
              <div className="d-flex align-items-center">
                <span style={usernameStyle}>Hello, {user?.username}</span>
                <button
                  className="btn btn-light ms-3"
                  onClick={handleLogout}
                  style={buttonStyle}
                >
                  Logout
                </button>
              </div>
            ) : (
              <>
                <Link className="btn btn-outline-light me-2" to="/login" style={buttonStyle}>
                  Login
                </Link>
                <Link className="btn btn-light" to="/register" style={buttonStyle}>
                  Register
                </Link>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

// Navbar styles
const navbarStyle = {
  background: 'linear-gradient(45deg, #1f1f1f, #2c2c2c)', // Darker gradient for the background
  padding: '1rem 2rem',
  fontSize: '1rem', // Slightly smaller font size for balance
  fontFamily: "'Poppins', sans-serif", // Modern font for a clean feel
  letterSpacing: '0.8px',
  boxShadow: '0 4px 20px rgba(0, 0, 0, 0.15)', // Soft shadow for depth
};

// Brand style
const brandStyle = {
  fontSize: '1.8rem',
  fontWeight: '600',
  color: '#ff6347', // Accent color for the brand name
  textTransform: 'uppercase',
  letterSpacing: '2px',
  textShadow: '2px 2px 5px rgba(0, 0, 0, 0.3)', // Subtle shadow effect for depth
  transition: 'color 0.3s ease, transform 0.3s ease',
  ':hover': {
    color: '#ff7f50', // Light orange color on hover
    transform: 'scale(1.05)', // Slight zoom effect
  },
};

// Link styles
const linkStyle = {
  color: '#f1f1f1', 
  textTransform: 'uppercase',
  fontWeight: '500',
  letterSpacing: '1px',
  padding: '10px 20px',
  textDecoration: 'none',
  transition: 'color 0.3s ease, transform 0.3s ease',
  ':hover': {
    color: '#ff6347', // Accent color on hover
    transform: 'scale(1.05)', // Slight zoom effect
  },
};

// Button styles
const buttonStyle = {
  padding: '8px 20px',
  fontSize: '0.95rem',
  fontWeight: '500',
  borderRadius: '30px',
  marginLeft: '15px',
  transition: 'background-color 0.3s ease, box-shadow 0.3s ease, transform 0.3s ease',
  ':hover': {
    backgroundColor: '#ff6347', // Warm color on hover
    transform: 'scale(1.05)', // Slight zoom effect
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.2)', // Shadow effect on hover
  },
};

// Username style
const usernameStyle = {
  color: '#f1f1f1',
  fontWeight: '600',
  fontSize: '1rem',
  marginRight: '10px',
};

export default Navbar;
