import React from 'react';
import { Link } from 'react-router-dom';

const Login = () => {
  return (
    <div
      style={{
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        background: 'linear-gradient(135deg, #001F3F, #001933)',
        padding: '20px',
      }}
    >
      <h1 style={{ color: '#FFDEB4', marginBottom: '30px' }}>SportSched Login</h1>
      <form
        style={{
          width: '100%',
          maxWidth: '350px',
          backgroundColor: 'rgba(255, 255, 255, 0.05)',
          padding: '25px',
          borderRadius: '15px',
          display: 'flex',
          flexDirection: 'column',
          gap: '15px',
          color: 'white',
        }}
      >
        <input type="text" placeholder="Username" style={inputStyle} />
        <input type="password" placeholder="Password" style={inputStyle} />
        <button style={buttonStyle}>Login</button>
        <p style={{ fontSize: '0.9rem', textAlign: 'center' }}>
          Don't have an account? <Link to="/register" style={{ color: '#FFD580' }}>Register</Link>
        </p>
      </form>
    </div>
  );
};

const inputStyle = {
  padding: '10px',
  borderRadius: '8px',
  border: '1px solid #ccc',
  fontSize: '1rem',
};

const buttonStyle = {
  backgroundColor: '#FFB347',
  color: '#000',
  padding: '10px',
  borderRadius: '8px',
  border: 'none',
  fontWeight: 'bold',
  cursor: 'pointer',
};

export default Login;
