// src/components/Facilities/FacilityDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../../services/api';

const FacilityDetail = () => {
  const { facilityId } = useParams();
  const [facility, setFacility] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchFacility = async () => {
      try {
        const response = await api.get(`facilities/${facilityId}/`);
        setFacility(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching facility:', error);
        setError('Failed to load facility details');
        setLoading(false);
      }
    };

    fetchFacility();
  }, [facilityId]);

  if (loading) return <div className="text-center mt-5 text-light">Loading...</div>;
  if (error) return <div className="alert alert-danger mt-3">{error}</div>;
  if (!facility) return <div className="alert alert-warning mt-3">Facility not found</div>;

  const imageSrc = facility.image || '/images/default-facility.png';

  return (
    <div
      style={{
        backgroundImage: `linear-gradient(rgba(0,0,0,0.7), rgba(0,0,0,0.85)), url('https://wallpapercave.com/wp/wp5861942.jpg')`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        fontFamily: 'Segoe UI, sans-serif',
        color: '#fff',
      }}
    >
      <div className="container py-5">
        <div className="text-center mb-5">
          <h1 className="display-4 fw-bold text-uppercase glow-title mb-2">{facility.name}</h1>
          <p className="lead text-light text-opacity-75">Experience it with <span className="text-warning fw-semibold">SportSched</span></p>
        </div>

        <div className="row justify-content-center g-4">
          <div className="col-md-7">
            <div
              className="glass-card p-5 h-100"
              style={{
                borderRadius: '1rem',
              }}
            >
              <h3 className="fw-semibold mb-3 text-info">About the Facility</h3>
              <p className="text-light">{facility.description}</p>
              <Link
                to={`/reserve/${facility.id}`}
                className="btn btn-warning btn-lg mt-4 glowing-btn"
              >
                Reserve Now
              </Link>
            </div>
          </div>

          <div className="col-md-5">
            <div className="image-container mb-4 rounded-4 overflow-hidden">
              <img
                src={imageSrc}
                alt={facility.name}
                className="img-fluid zoom-img"
                style={{
                  borderRadius: '1rem',
                  transition: 'transform 0.5s ease-in-out',
                }}
              />
            </div>

            <div className="glass-card p-4">
              <h5 className="fw-bold text-info">Quick Info</h5>
              <ul className="mt-3 ps-3">
                <li><strong>Type:</strong> {facility.facility_type}</li>
                <li><strong>Open to:</strong> Students, Faculty, Staff</li>
                <li><strong>Equipment:</strong> Available on request</li>
                <li><strong>Max Duration:</strong> 2 hours per booking</li>
              </ul>
            </div>
          </div>
        </div>
      </div>

      <style>{`
        .glass-card {
          background: rgba(255, 255, 255, 0.08);
          border: 1px solid rgba(255, 255, 255, 0.2);
          backdrop-filter: blur(10px);
          box-shadow: 0 0 25px rgba(0, 255, 204, 0.15);
          transition: all 0.3s ease;
          color: #e3e3e3;
        }

        .glass-card:hover {
          box-shadow: 0 0 40px rgba(0, 255, 204, 0.25);
        }

        .glowing-btn {
          box-shadow: 0 0 20px rgba(255, 193, 7, 0.6);
          transition: all 0.3s ease-in-out;
        }

        .glowing-btn:hover {
          transform: scale(1.05);
          box-shadow: 0 0 35px rgba(255, 193, 7, 0.9);
        }

        .zoom-img:hover {
          transform: scale(1.07);
        }

        .glow-title {
          text-shadow: 0 0 15px rgba(0, 255, 204, 0.6);
          animation: fadeInUp 1s ease-in-out;
        }

        @keyframes fadeInUp {
          0% {
            opacity: 0;
            transform: translateY(20px);
          }
          100% {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </div>
  );
};

export default FacilityDetail;
