import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../../services/api';

const FacilityDetail = () => {
  const { facilityId } = useParams();
  const [facility, setFacility] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchFacility = async () => {
      try {
        const response = await api.get(`facilities/${facilityId}/`);
        setFacility(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching facility:', error);
        setError('Failed to load facility details');
        setLoading(false);
      }
    };

    fetchFacility();
  }, [facilityId]);

  if (loading) return <div className="text-center mt-5 text-white">Loading...</div>;
  if (error) return <div className="alert alert-danger mt-3">{error}</div>;
  if (!facility) return <div className="alert alert-warning mt-3">Facility not found</div>;

  const imageSrc = facility.image || '/images/default-facility.png';

  return (
    <div
      style={{
        backgroundImage: `url('${imageSrc}')`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        position: 'relative',
        overflow: 'hidden',
        fontFamily: 'Segoe UI, sans-serif',
        color: '#fff',
      }}
    >
      {/* Blurred Overlay */}
      <div
        style={{
          position: 'absolute',
          top: 0, left: 0, right: 0, bottom: 0,
          backdropFilter: 'blur(10px) brightness(0.4)',
          zIndex: 1,
        }}
      ></div>

      {/* Content Box */}
      <div
        className="p-5 text-center"
        style={{
          background: 'rgba(255, 255, 255, 0.08)',
          borderRadius: '25px',
          border: '1px solid rgba(255,255,255,0.15)',
          zIndex: 2,
          maxWidth: '800px',
          width: '90%',
          boxShadow: '0 0 50px rgba(0,0,0,0.4)',
          animation: 'fadeInUp 1s ease-out',
        }}
      >
        <h1
          className="display-4 fw-bold text-uppercase mb-3"
          style={{ letterSpacing: '2px', color: '#fff' }}
        >
          {facility.name}
        </h1>
        <p className="lead mb-4 text-light text-opacity-75">
          <strong>Type:</strong> {facility.facility_type}
        </p>
        <p className="mb-4" style={{ fontSize: '1.1rem', color: '#eaeaea' }}>
          {facility.description}
        </p>

        <ul
          className="list-unstyled text-start mx-auto"
          style={{ maxWidth: '500px', color: '#d1d1d1' }}
        >
          <li className="mb-2">üéØ <strong>Access:</strong> Students, Faculty, Staff</li>
          <li className="mb-2">üéæ <strong>Equipment:</strong> Available on request</li>
          <li className="mb-2">‚è±Ô∏è <strong>Booking Time Limit:</strong> 2 hours</li>
        </ul>

        <Link
          to={`/reserve/${facility.id}`}
          className="btn btn-lg mt-4 px-5 glow-btn"
          style={{
            backgroundColor: '#ffc107',
            color: '#000',
            fontWeight: 'bold',
            borderRadius: '50px',
            boxShadow: '0 0 15px rgba(255,193,7,0.6)',
            transition: 'all 0.3s ease-in-out',
          }}
        >
          Reserve Now
        </Link>
      </div>

      {/* Animations */}
      <style>
        {`
          @keyframes fadeInUp {
            0% { opacity: 0; transform: translateY(40px); }
            100% { opacity: 1; transform: translateY(0); }
          }

          .glow-btn:hover {
            background-color: #ffca2c;
            transform: scale(1.05);
            box-shadow: 0 0 25px rgba(255,193,7,0.9);
          }
        `}
      </style>
    </div>
  );
};

export default FacilityDetail;
