// src/components/Facilities/FacilityDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../../services/api';

const FacilityDetail = () => {
  const { facilityId } = useParams();
  const [facility, setFacility] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchFacility = async () => {
      try {
        const response = await api.get(`facilities/${facilityId}/`);
        setFacility(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching facility:', error);
        setError('Failed to load facility details');
        setLoading(false);
      }
    };

    fetchFacility();
  }, [facilityId]);

  if (loading) return <div className="text-center mt-5 text-white">Loading...</div>;
  if (error) return <div className="alert alert-danger mt-3">{error}</div>;
  if (!facility) return <div className="alert alert-warning mt-3">Facility not found</div>;

  const imageSrc = facility.image || '/images/default-facility.png';

  return (
    <div
      style={{
        minHeight: '100vh',
        backgroundImage: `linear-gradient(rgba(0, 0, 30, 0.85), rgba(0, 0, 40, 0.9)), url('https://t4.ftcdn.net/jpg/02/81/79/13/360_F_281791395_yMsSMTbxDZ9s7neOIln5yeIyKtSD0Tdo.jpg')`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        padding: '60px 20px',
        fontFamily: 'Segoe UI, sans-serif',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <div
        style={{
          maxWidth: '1000px',
          width: '100%',
          background: 'rgba(255, 255, 255, 0.05)',
          border: '1px solid rgba(255, 255, 255, 0.1)',
          borderRadius: '20px',
          backdropFilter: 'blur(10px)',
          boxShadow: '0 0 30px rgba(65,105,225,0.15)',
          color: '#fff',
          padding: '40px',
          textAlign: 'center',
        }}
      >
        <h1 className="mb-4 fw-bold" style={{ fontSize: '2.8rem', letterSpacing: '1.5px' }}>
          {facility.name}
        </h1>
        <p className="text-light text-opacity-75 mb-4" style={{ fontSize: '1.2rem' }}>
          Facility Type: <strong>{facility.facility_type}</strong>
        </p>

        <div
          className="image-container mb-4"
          style={{
            overflow: 'hidden',
            borderRadius: '15px',
            maxHeight: '350px',
            boxShadow: '0 6px 20px rgba(0,0,0,0.5)',
          }}
        >
          <img
            src={imageSrc}
            alt={facility.name}
            className="img-fluid"
            style={{
              width: '100%',
              objectFit: 'cover',
              transition: 'transform 0.3s ease',
            }}
            onMouseOver={(e) => (e.currentTarget.style.transform = 'scale(1.03)')}
            onMouseOut={(e) => (e.currentTarget.style.transform = 'scale(1)')}
          />
        </div>

        <p style={{ fontSize: '1.1rem', color: '#e0e0e0', marginBottom: '30px' }}>
          {facility.description}
        </p>

        <div className="d-flex justify-content-center gap-3 flex-wrap">
          <Link
            to={`/reserve/${facility.id}`}
            className="btn btn-primary px-4 py-2 glow-btn"
            style={{
              backgroundColor: '#4169e1',
              border: 'none',
              fontSize: '1rem',
              borderRadius: '8px',
            }}
          >
            Reserve This Facility
          </Link>
          <Link
            to="/facilities"
            className="btn btn-outline-light px-4 py-2"
            style={{
              borderColor: 'rgba(255,255,255,0.4)',
              fontSize: '1rem',
              borderRadius: '8px',
            }}
          >
            Back to List
          </Link>
        </div>
      </div>

      {/* Styles */}
      <style>
        {`
          .glow-btn {
            box-shadow: 0 0 10px rgba(65,105,225, 0.4);
            transition: all 0.3s ease-in-out;
          }

          .glow-btn:hover {
            box-shadow: 0 0 18px rgba(65,105,225, 0.65);
            transform: scale(1.04);
          }
        `}
      </style>
    </div>
  );
};

export default FacilityDetail;
