import React, { useState, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const { login, isAuthenticated } = useContext(AuthContext);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError('Please enter both username and password');
      return;
    }
    const success = await login(username, password);
    if (success) {
      navigate('/facilities');
    } else {
      setError('Invalid credentials. Please try again.');
    }
  };

  if (isAuthenticated) {
    navigate('/facilities');
  }

  const styles = {
    page: {
      display: 'flex',
      flexDirection: 'column',
      height: '100vh',
      backgroundImage: 'url("https://img.freepik.com/premium-photo/sports-background-advertising-sport-life-concept-generative-ai_1002555-984.jpg?w=2000")',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      alignItems: 'center',
      justifyContent: 'center',
      fontFamily: 'Calibri, sans-serif',
    },
    heading: {
      color: '#ffffff',
      fontSize: '40px',
      fontWeight: 'bold',
      marginBottom: '20px',
      textShadow: '2px 2px 6px #000',
    },
    form: {
      backgroundColor: 'rgba(0, 0, 0, 0.7)',
      padding: '30px',
      borderRadius: '10px',
      width: '100%',
      maxWidth: '400px',
      color: '#f8f8f8',
      boxShadow: '0px 4px 15px rgba(0, 0, 0, 0.6)',
    },
    label: {
      marginBottom: '5px',
      display: 'block',
    },
    input: {
      width: '100%',
      padding: '10px',
      marginBottom: '15px',
      border: '1px solid #ccc',
      borderRadius: '4px',
      fontFamily: 'Calibri, sans-serif',
      backgroundColor: '#001f3f',
      color: '#fff',
    },
    button: {
      width: '100%',
      padding: '10px',
      backgroundColor: '#001f7f',
      color: '#fff',
      fontWeight: 'bold',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
    },
    error: {
      color: '#C62828',
      backgroundColor: '#FFEBEE',
      padding: '10px',
      borderRadius: '5px',
      marginBottom: '15px',
      textAlign: 'center',
    },
  };

  return (
    <div style={styles.page}>
      <div style={styles.heading}>Welcome to SportSched</div>
      <form onSubmit={handleLogin} style={styles.form}>
        <label style={styles.label} htmlFor="username">Email address</label>
        <input
          type="text"
          id="username"
          style={styles.input}
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Enter your email"
          required
        />
        <label style={styles.label} htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          style={styles.input}
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Enter your password"
          required
        />
        {error && <div style={styles.error}>{error}</div>}
        <button type="submit" style={styles.button}>Sign in now</button>
      </form>
    </div>
  );
};

export default Login;
