// src/components/Auth/Login.js
import React, { useState, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';  // Import AuthContext here
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const { login, isAuthenticated } = useContext(AuthContext); // Using the AuthContext
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!username || !password) {
      setError('Please enter both username and password');
      return;
    }

    const success = await login(username, password);
    if (success) {
      navigate('/facilities'); // Redirect to facilities page if login is successful
    } else {
      setError('Invalid credentials. Please try again.');
    }
  };

  if (isAuthenticated) {
    navigate('/facilities'); // Redirect to facilities if already authenticated
  }

  const styles = {
    body: {
      backgroundColor: '#212121', // Dark charcoal background
      color: '#E0E0E0', // Light gray text color
      fontFamily: 'Arial, sans-serif',
      margin: 0,
      padding: 0,
    },
    loginContainer: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      backgroundColor: '#212121', // Dark charcoal background
    },
    loginBox: {
      backgroundColor: '#333333', // Dark box background
      padding: '30px',
      borderRadius: '8px',
      width: '100%',
      maxWidth: '400px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    },
    heading: {
      textAlign: 'center',
      marginBottom: '20px',
      color: '#FFFFFF', // White color for the heading
    },
    formLabel: {
      color: '#B0BEC5', // Light gray for labels
    },
    formControl: {
      backgroundColor: '#424242', // Slightly lighter dark gray for inputs
      color: '#E0E0E0', // Light gray text for inputs
      border: '1px solid #616161', // Gray border
      marginBottom: '15px',
      padding: '10px',
      width: '100%',
    },
    formControlFocus: {
      borderColor: '#388E3C', // Emerald green border on focus
      boxShadow: '0 0 0 0.2rem rgba(56, 142, 60, 0.25)', // Emerald green glow on focus
    },
    btnPrimary: {
      backgroundColor: '#388E3C', // Emerald green button
      borderColor: '#388E3C',
      width: '100%',
      padding: '10px',
      borderRadius: '5px',
      cursor: 'pointer',
      color: '#FFFFFF', // White text on button
      fontWeight: 'bold',
    },
    btnPrimaryHover: {
      backgroundColor: '#2C6F31', // Darker green on hover
      borderColor: '#2C6F31',
    },
    alert: {
      marginTop: '15px',
      backgroundColor: '#F8D7DA', // Light red for error messages
      color: '#721C24', // Dark red text for error
      borderRadius: '5px',
      padding: '10px',
      textAlign: 'center',
    },
  };

  return (
    <div style={styles.loginContainer}>
      <div style={styles.loginBox}>
        <h2 style={styles.heading}>Login</h2>
        <form onSubmit={handleLogin}>
          <div>
            <label htmlFor="username" style={styles.formLabel}>Username</label>
            <input 
              type="text" 
              id="username" 
              style={styles.formControl} 
              value={username} 
              onChange={(e) => setUsername(e.target.value)} 
              required 
            />
          </div>
          <div>
            <label htmlFor="password" style={styles.formLabel}>Password</label>
            <input 
              type="password" 
              id="password" 
              style={styles.formControl} 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              required 
            />
          </div>
          {error && <div style={styles.alert}>{error}</div>}
          <button 
            type="submit" 
            style={styles.btnPrimary} 
            onMouseOver={(e) => e.target.style.backgroundColor = styles.btnPrimaryHover.backgroundColor}
            onMouseOut={(e) => e.target.style.backgroundColor = styles.btnPrimary.backgroundColor}
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
