// src/components/Reservations/MyReservations.js
import React, { useState, useEffect } from 'react';
import api from '../../services/api';

const MyReservations = () => {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchReservations = async () => {
      try {
        const response = await api.get('reservations/');
        setReservations(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching reservations:', error);
        setLoading(false);
      }
    };

    fetchReservations();
  }, []);

  const handleCancelReservation = async (id) => {
    if (window.confirm('Are you sure you want to cancel this reservation?')) {
      try {
        await api.delete(`reservations/${id}/`);
        setReservations(reservations.filter(reservation => reservation.id !== id));
      } catch (error) {
        console.error('Error canceling reservation:', error);
        alert('Failed to cancel reservation. Please try again.');
      }
    }
  };

  if (loading) return <div className="text-center mt-5">Loading...</div>;

  return (
    <div
      style={{
        backgroundImage: `url('https://your-background-image-url.jpg')`, // Use your background image URL
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        color: '#fff',
        fontFamily: 'Segoe UI, sans-serif',
        padding: '80px 0',
        textAlign: 'center',
        backgroundAttachment: 'fixed',
      }}
    >
      {/* Dark Overlay */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.6)', // Dark overlay
          zIndex: -1,
        }}
      ></div>

      {/* Main Title */}
      <div className="container mb-5">
        <h2 className="text-center text-light fw-bold" style={{ fontSize: '3rem', textShadow: '5px 5px 15px rgba(0,0,0,0.8)' }}>
          My Reservations
        </h2>
      </div>

      {/* Content */}
      {reservations.length === 0 ? (
        <div className="alert alert-info text-center" style={{ backgroundColor: 'rgba(0, 0, 0, 0.7)', borderRadius: '10px' }}>
          You have no reservations yet.
        </div>
      ) : (
        <div className="container table-responsive">
          <table
            className="table table-striped table-hover"
            style={{
              backgroundColor: 'rgba(0, 0, 0, 0.7)', // Dark background for the table
              backdropFilter: 'blur(15px)', // Added blur for that sleek modern effect
              borderRadius: '15px',
              boxShadow: '0 8px 25px rgba(0, 0, 0, 0.6)',
              color: '#fff',
            }}
          >
            <thead>
              <tr>
                <th>Facility</th>
                <th>Date</th>
                <th>Time</th>
                <th>Booked On</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {reservations.map((reservation) => (
                <tr key={reservation.id}>
                  <td>{reservation.facility_name}</td>
                  <td>{new Date(reservation.time_slot_details.date).toLocaleDateString()}</td>
                  <td>
                    {new Date(`2000-01-01T${reservation.time_slot_details.start_time}`).toLocaleTimeString([], {
                      hour: '2-digit',
                      minute: '2-digit',
                    })}{' '}
                    -{' '}
                    {new Date(`2000-01-01T${reservation.time_slot_details.end_time}`).toLocaleTimeString([], {
                      hour: '2-digit',
                      minute: '2-digit',
                    })}
                  </td>
                  <td>{new Date(reservation.created_at).toLocaleDateString()}</td>
                  <td>
                    <button
                      className="btn btn-sm btn-danger"
                      onClick={() => handleCancelReservation(reservation.id)}
                      style={{
                        backgroundColor: '#FF5757',
                        borderRadius: '5px',
                        transition: 'background-color 0.4s ease',
                      }}
                      onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = '#FF3333')}
                      onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = '#FF5757')}
                    >
                      Cancel
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Footer Section - SportSched Branding */}
      <div className="text-center mt-5" style={{ textShadow: '2px 2px 8px rgba(0, 0, 0, 0.6)' }}>
        <h3 style={{ fontSize: '1.5rem', letterSpacing: '1px', fontWeight: 'bold' }}>Powered by <span style={{ color: '#003366' }}>SportSched</span></h3>
      </div>

      {/* Custom Styles */}
      <style>
        {`
          table thead {
            background-color: rgba(0, 0, 0, 0.8);
            color: #fff;
            font-weight: bold;
            text-transform: uppercase;
          }

          table tbody tr:hover {
            background-color: rgba(255, 255, 255, 0.1);
            cursor: pointer;
            box-shadow: 0 5px 20px rgba(0, 0, 0, 0.4);
            transform: scale(1.03);
            transition: all 0.3s ease-in-out;
          }

          table th, table td {
            padding: 15px;
            font-size: 1.1rem;
            text-align: center;
          }

          .table-responsive {
            max-width: 1200px;
            margin: auto;
          }

          .btn-danger {
            border: none;
            box-shadow: 0 3px 15px rgba(255, 87, 34, 0.6);
            transition: box-shadow 0.4s ease;
          }

          .btn-danger:hover {
            box-shadow: 0 6px 30px rgba(255, 87, 34, 0.8);
            transform: scale(1.05);
          }
        `}
      </style>
    </div>
  );
};

export default MyReservations;
