import React, { useState, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const { login, isAuthenticated } = useContext(AuthContext);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!username || !password) {
      setError('Please enter both username and password');
      return;
    }

    const success = await login(username, password);
    if (success) {
      navigate('/facilities');
    } else {
      setError('Invalid credentials. Please try again.');
    }
  };

  if (isAuthenticated) {
    navigate('/facilities');
  }

  const styles = {
    body: {
      backgroundImage: 'url("https://img.freepik.com/premium-photo/sports-background-advertising-sport-life-concept-generative-ai_1002555-984.jpg?w=2000")',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundAttachment: 'fixed',
      color: '#F3F4F6', // Off-white text color
      fontFamily: 'Arial, sans-serif',
      margin: 0,
      padding: 0,
      height: '100vh',
    },
    loginContainer: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      animation: 'fadeIn 1s ease-in-out',
    },
    loginBox: {
      backgroundColor: '#000000', // Black background
      padding: '30px',
      borderRadius: '8px',
      width: '100%',
      maxWidth: '400px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.5)',
      animation: 'slideUp 1s ease-out',
    },
    heading: {
      textAlign: 'center',
      marginBottom: '20px',
      color: '#FFFFFF',
      fontSize: '28px',
      fontWeight: 'bold',
      animation: 'fadeIn 1s ease-in-out',
    },
    formLabel: {
      color: '#B0BEC5', // Light grey text for labels
    },
    formControl: {
      backgroundColor: '#2C3E50',
      color: '#F3F4F6',
      border: '1px solid #616161',
      marginBottom: '15px',
      padding: '10px',
      width: '100%',
      transition: 'all 0.3s ease',
    },
    formControlFocus: {
      borderColor: '#FFD700',
      boxShadow: '0 0 0 0.2rem rgba(255, 215, 0, 0.25)',
    },
    btnPrimary: {
      backgroundColor: '#003366', // Dark Blue button
      borderColor: '#003366',
      width: '100%',
      padding: '10px',
      borderRadius: '5px',
      cursor: 'pointer',
      color: '#FFFFFF',
      fontWeight: 'bold',
      transition: 'background-color 0.3s ease',
    },
    btnPrimaryHover: {
      backgroundColor: '#00224d', // Darker Blue
      borderColor: '#00224d',
    },
    alert: {
      marginTop: '15px',
      backgroundColor: '#F6D0D0',
      color: '#721C24',
      borderRadius: '5px',
      padding: '10px',
      textAlign: 'center',
    },
    navbar: {
      backgroundColor: '#000000', // Black navbar
      padding: '20px 0',
      fontSize: '20px',
      color: '#ffffff',
      textAlign: 'center',
      height: '60px', // Adjust height of navbar
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    },
    mainContent: {
      paddingTop: '60px', // Add padding to push the content below the navbar
    },
  };

  return (
    <div style={styles.body}> {/* Apply styles here */}
      <div style={styles.navbar}>
        {/* Removed the "facilities" text from navbar as you requested */}
        Sports Application
      </div>
      <div style={styles.mainContent}>
        <div style={styles.loginContainer}>
          <div style={styles.loginBox}>
            <h2 style={styles.heading}>Login</h2>
            <form onSubmit={handleLogin}>
              <div>
                <label htmlFor="username" style={styles.formLabel}>Username</label>
                <input 
                  type="text" 
                  id="username" 
                  style={styles.formControl} 
                  value={username} 
                  onChange={(e) => setUsername(e.target.value)} 
                  required 
                />
              </div>
              <div>
                <label htmlFor="password" style={styles.formLabel}>Password</label>
                <input 
                  type="password" 
                  id="password" 
                  style={styles.formControl} 
                  value={password} 
                  onChange={(e) => setPassword(e.target.value)} 
                  required 
                />
              </div>
              {error && <div style={styles.alert}>{error}</div>}
              <button 
                type="submit" 
                style={styles.btnPrimary} 
                onMouseOver={(e) => e.target.style.backgroundColor = styles.btnPrimaryHover.backgroundColor}
                onMouseOut={(e) => e.target.style.backgroundColor = styles.btnPrimary.backgroundColor}
              >
                Login
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
