// src/pages/Login.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Login = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle login logic here
  };

  return (
    <div style={{
      display: 'flex',
      minHeight: '100vh',
      flexDirection: 'row',
      background: 'url("/bg-sports.jpg") center/cover no-repeat',
    }}>
      <div style={{
        flex: 1,
        background: 'rgba(0, 0, 0, 0.6)',
        color: 'white',
        padding: '40px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
      }}>
        <h1 style={{ fontSize: '3rem', color: '#FFDEB4', marginBottom: '20px' }}>SportSched</h1>
        <p style={{ fontSize: '1.2rem', maxWidth: '500px' }}>
          Welcome back! Book your favorite sports facilities in seconds.
        </p>
      </div>

      <div style={{
        flex: 1,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '40px',
      }}>
        <form
          onSubmit={handleSubmit}
          style={{
            width: '100%',
            maxWidth: '400px',
            background: 'rgba(255,255,255,0.1)',
            padding: '30px',
            borderRadius: '20px',
            color: 'white',
            backdropFilter: 'blur(10px)',
          }}
        >
          <h2 style={{ marginBottom: '25px', textAlign: 'center', color: '#FFD580' }}>Login</h2>

          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            style={{
              width: '100%',
              padding: '10px',
              marginBottom: '15px',
              borderRadius: '8px',
              border: 'none',
              outline: 'none',
            }}
          />

          <label>Password:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
            style={{
              width: '100%',
              padding: '10px',
              marginBottom: '20px',
              borderRadius: '8px',
              border: 'none',
              outline: 'none',
            }}
          />

          <button
            type="submit"
            style={{
              width: '100%',
              padding: '12px',
              borderRadius: '10px',
              backgroundColor: '#FFB347',
              color: 'black',
              fontWeight: 'bold',
              border: 'none',
              fontSize: '1rem',
              cursor: 'pointer',
            }}
          >
            Login
          </button>

          <p style={{ marginTop: '15px', textAlign: 'center' }}>
            Donâ€™t have an account? <Link to="/register" style={{ color: '#FFDEB4' }}>Register</Link>
          </p>
        </form>
      </div>

      {/* RESPONSIVE FIXES */}
      <style>
        {`
          @media (max-width: 768px) {
            div[style*="flex-direction: row"] {
              flex-direction: column;
            }

            div[style*="flex: 1; background: rgba(0, 0, 0, 0.6)"] {
              padding: 20px;
              text-align: center;
            }

            div[style*="flex: 1; display: flex; justify-content: center"] {
              padding: 20px;
            }
          }
        `}
      </style>
    </div>
  );
};

export default Login;
