// src/components/Common/Navbar.js
import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';

const Navbar = () => {
  const { isAuthenticated, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = async () => {
    await logout();
    navigate('/login');
  };

  return (
    <nav
      className="navbar navbar-expand-lg navbar-dark"
      style={{
        background: 'linear-gradient(45deg, #2c3e50, #34495e)', // Darker gradient for the background
        padding: '1rem 3rem',
        fontSize: '1rem', // Smaller font size
        borderRadius: '0 0 20px 20px', // Rounded bottom edges
        boxShadow: '0px 5px 15px rgba(0, 0, 0, 0.2)', // Soft shadow for depth
        fontFamily: "'Poppins', sans-serif", // Modern font
      }}
    >
      <div className="container">
        <Link
          className="navbar-brand"
          to="/"
          style={{
            fontSize: '2rem', // Slightly smaller font for brand name
            fontWeight: '700',
            letterSpacing: '1px',
            color: '#fff',
            textShadow: '2px 2px 10px rgba(0, 0, 0, 0.4)',
            textTransform: 'uppercase',
          }}
        >
          College Sports Reservation
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto">
            <li className="nav-item">
              <Link className="nav-link" to="/facilities" style={linkStyle}>
                Facilities
              </Link>
            </li>
            {isAuthenticated && (
              <li className="nav-item">
                <Link className="nav-link" to="/my-reservations" style={linkStyle}>
                  My Reservations
                </Link>
              </li>
            )}
          </ul>
          <div className="d-flex ms-auto">
            {isAuthenticated ? (
              <button className="btn btn-light" onClick={handleLogout} style={buttonStyle}>
                Logout
              </button>
            ) : (
              <>
                <Link className="btn btn-outline-light me-3" to="/login" style={buttonStyle}>
                  Login
                </Link>
                <Link className="btn btn-light" to="/register" style={buttonStyle}>
                  Register
                </Link>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

// Styling for links and buttons
const linkStyle = {
  fontWeight: '500',
  color: '#fff',
  textTransform: 'uppercase',
  letterSpacing: '1.5px',
  transition: 'color 0.3s ease, transform 0.3s ease',
  padding: '8px 12px', // Better padding for links
  margin: '0 8px',
  textDecoration: 'none', // Removing underline on hover
};

// Button Styling with hover effects
const buttonStyle = {
  fontSize: '1rem',
  fontWeight: '600',
  borderRadius: '50px',
  padding: '10px 20px',
  transition: 'transform 0.3s ease, box-shadow 0.3s ease, background-color 0.3s ease',
  marginLeft: '10px',
};

// Hover effects for links and buttons
const hoverLinkStyle = {
  ...linkStyle,
  ':hover': {
    color: '#f39c12', // Golden color on hover for links
    transform: 'scale(1.1)', // Scale effect
  },
};

const hoverButtonStyle = {
  ...buttonStyle,
  ':hover': {
    backgroundColor: '#2980b9', // Dark blue on hover for buttons
    transform: 'scale(1.05)', // Slight button scale effect
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.2)',
  },
};

export default Navbar;
