// src/components/Facilities/FacilityList.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getFacilities } from '../../services/api';

const FacilityList = () => {
  const [facilities, setFacilities] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchFacilities = async () => {
      try {
        const response = await getFacilities();
        setFacilities(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching facilities:', error);
        setLoading(false);
      }
    };

    fetchFacilities();
  }, []);

  const getBackgroundColor = (type) => {
    switch (type.toLowerCase()) {
      case 'basketball': return '#FFA07A';
      case 'tennis': return '#98FB98';
      case 'swimming': return '#87CEFA';
      case 'football': return '#FFD700';
      default: return '#f0f0f0';
    }
  };

  return (
    <div style={{
      backgroundColor: '#001F3F',
      minHeight: '100vh',
      padding: '40px 20px',
      color: 'white',
      fontFamily: 'Segoe UI, sans-serif'
    }}>
      <h1 style={{ textAlign: 'center', fontSize: '42px', fontWeight: 'bold', color: '#FFDEB4', marginBottom: '30px' }}>
        SportSched Facilities
      </h1>

      {loading ? (
        <div className="text-center"><div className="spinner-border"></div></div>
      ) : (
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
          gap: '20px'
        }}>
          {facilities.map(facility => (
            <div
              key={facility.id}
              style={{
                backgroundColor: getBackgroundColor(facility.type),
                padding: '20px',
                borderRadius: '15px',
                boxShadow: '0 0 20px rgba(0, 0, 0, 0.3)',
                cursor: 'pointer',
                transition: 'transform 0.2s ease-in-out',
              }}
              onClick={() => navigate(`/facility/${facility.id}`)}
            >
              <h3 style={{ fontSize: '22px', fontWeight: 'bold', color: '#000' }}>{facility.name}</h3>
              <p style={{ fontSize: '16px', color: '#333', marginBottom: 0 }}>{facility.type}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FacilityList;
