// src/components/Common/Navbar.js
import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';

const Navbar = () => {
  const { isAuthenticated, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = async () => {
    await logout();
    navigate('/login');
  };

  return (
    <nav
      className="navbar navbar-expand-lg navbar-dark"
      style={{
        background: '#2D3E50', // Dark and rich background
        padding: '1.2rem 3rem',
        fontSize: '1rem',
        borderRadius: '0 0 15px 15px', // Rounded bottom edges
        boxShadow: '0px 10px 20px rgba(0, 0, 0, 0.25)', // Deeper shadow for more focus
        fontFamily: "'Roboto', sans-serif", // Clean, modern font
      }}
    >
      <div className="container">
        <Link
          className="navbar-brand"
          to="/"
          style={{
            fontSize: '1.9rem', // Slightly smaller, but bold
            fontWeight: '600',
            color: '#ffffff',
            letterSpacing: '1px',
            textTransform: 'uppercase',
            textShadow: '2px 2px 15px rgba(0, 0, 0, 0.5)', // Text shadow for extra pop
          }}
        >
          College Sports Reservation
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto">
            <li className="nav-item">
              <Link className="nav-link" to="/facilities" style={linkStyle}>
                Facilities
              </Link>
            </li>
            {isAuthenticated && (
              <li className="nav-item">
                <Link className="nav-link" to="/my-reservations" style={linkStyle}>
                  My Reservations
                </Link>
              </li>
            )}
          </ul>
          <div className="d-flex ms-auto">
            {isAuthenticated ? (
              <button className="btn btn-light" onClick={handleLogout} style={buttonStyle}>
                Logout
              </button>
            ) : (
              <>
                <Link className="btn btn-outline-light me-3" to="/login" style={buttonStyle}>
                  Login
                </Link>
                <Link className="btn btn-light" to="/register" style={buttonStyle}>
                  Register
                </Link>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

// Navbar link style
const linkStyle = {
  fontWeight: '500',
  color: '#f1f1f1',
  textTransform: 'uppercase',
  letterSpacing: '1px',
  padding: '8px 16px',
  margin: '0 12px',
  textDecoration: 'none', // Remove underline by default
  transition: 'color 0.3s ease, transform 0.3s ease', // Smooth transition
};

// Button styling with hover effect
const buttonStyle = {
  fontSize: '1rem',
  fontWeight: '600',
  borderRadius: '30px', // Round buttons
  padding: '10px 25px',
  transition: 'background-color 0.3s ease, transform 0.3s ease, box-shadow 0.3s ease',
  marginLeft: '15px',
};

// Hover effect for links
const hoverLinkStyle = {
  ...linkStyle,
  ':hover': {
    color: '#e74c3c', // Red color on hover
    transform: 'scale(1.05)', // Subtle scaling effect
  },
};

// Hover effect for buttons
const hoverButtonStyle = {
  ...buttonStyle,
  ':hover': {
    backgroundColor: '#16a085', // Greenish blue color on hover
    transform: 'scale(1.05)',
    boxShadow: '0px 5px 15px rgba(0, 0, 0, 0.2)', // Shadow for depth
  },
};

export default Navbar;
