// src/components/Common/Navbar.js
import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';

const Navbar = () => {
  const { isAuthenticated, logout, user } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = async () => {
    await logout();
    navigate('/login');
  };

  return (
    <nav
      className="navbar navbar-expand-lg navbar-dark"
      style={{
        background: 'linear-gradient(90deg, #6a11cb 0%, #2575fc 100%)', // Beautiful gradient
        padding: '1rem 2rem',
        fontSize: '1.2rem',
        borderRadius: '0 0 15px 15px',
        boxShadow: '0px 4px 15px rgba(0, 0, 0, 0.1)',
      }}
    >
      <div className="container">
        <Link
          className="navbar-brand"
          to="/"
          style={{
            fontSize: '2rem',
            fontWeight: '700',
            letterSpacing: '2px',
            color: '#fff',
            textShadow: '2px 2px 5px rgba(0, 0, 0, 0.4)',
          }}
        >
          College Sports Reservation
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav me-auto">
            <li className="nav-item">
              <Link className="nav-link" to="/facilities" style={linkStyle}>
                Facilities
              </Link>
            </li>
            {isAuthenticated && (
              <li className="nav-item">
                <Link className="nav-link" to="/my-reservations" style={linkStyle}>
                  My Reservations
                </Link>
              </li>
            )}
          </ul>
          <div className="d-flex">
            {isAuthenticated ? (
              <>
                <span
                  className="navbar-text"
                  style={{
                    color: '#fff',
                    fontSize: '1.1rem',
                    marginRight: '15px',
                    fontWeight: '600',
                  }}
                >
                  Welcome, {user?.username}!
                </span>
                <button className="btn btn-light" onClick={handleLogout}>
                  Logout
                </button>
              </>
            ) : (
              <>
                <Link className="btn btn-outline-light me-2" to="/login" style={buttonStyle}>
                  Login
                </Link>
                <Link className="btn btn-light" to="/register" style={buttonStyle}>
                  Register
                </Link>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

// Styling for links and buttons
const linkStyle = {
  fontWeight: '500',
  color: '#fff',
  textTransform: 'uppercase',
  letterSpacing: '1px',
  transition: 'color 0.3s ease',
};

const buttonStyle = {
  fontSize: '1rem',
  fontWeight: '600',
  borderRadius: '50px',
  transition: 'transform 0.3s ease, box-shadow 0.3s ease',
};

export default Navbar;
