// src/components/Reservations/MyReservations.js
import React, { useState, useEffect } from 'react';
import api from '../../services/api';

const MyReservations = () => {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchReservations = async () => {
      try {
        const response = await api.get('reservations/');
        setReservations(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching reservations:', error);
        setLoading(false);
      }
    };

    fetchReservations();
  }, []);

  const handleCancelReservation = async (id) => {
    if (window.confirm('Are you sure you want to cancel this reservation?')) {
      try {
        await api.delete(`reservations/${id}/`);
        setReservations(reservations.filter(reservation => reservation.id !== id));
      } catch (error) {
        console.error('Error canceling reservation:', error);
        alert('Failed to cancel reservation. Please try again.');
      }
    }
  };

  if (loading) return <div className="text-center mt-5">Loading...</div>;

  return (
    <div
      style={{
        backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)), url("https://your-background-image-url.jpg")`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        fontFamily: 'Arial, sans-serif',
        color: '#f8f9fa',
        padding: '60px 0',
        textAlign: 'center',
        backgroundAttachment: 'fixed',
      }}
    >
      <div className="container">
        <h2 className="text-center text-light display-3 fw-bold mb-4" style={{ textShadow: '2px 2px 10px rgba(0,0,0,0.8)' }}>
          My Reservations
        </h2>

        {reservations.length === 0 ? (
          <div className="alert alert-info text-center">You have no reservations yet.</div>
        ) : (
          <div className="table-responsive">
            <table className="table table-striped table-hover" style={{
              backdropFilter: 'blur(15px)',
              borderRadius: '1rem',
              backgroundColor: 'rgba(255, 255, 255, 0.1)',
              boxShadow: '0 4px 12px rgba(0, 0, 0, 0.3)',
              padding: '20px'
            }}>
              <thead style={{ color: '#218838' }}>
                <tr>
                  <th>Facility</th>
                  <th>Date</th>
                  <th>Time</th>
                  <th>Booked On</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {reservations.map(reservation => (
                  <tr key={reservation.id} className="table-row" style={{ transition: 'transform 0.2s ease' }}>
                    <td>{reservation.facility_name}</td>
                    <td>{new Date(reservation.time_slot_details.date).toLocaleDateString()}</td>
                    <td>
                      {new Date(`2000-01-01T${reservation.time_slot_details.start_time}`).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })} 
                      - 
                      {new Date(`2000-01-01T${reservation.time_slot_details.end_time}`).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    </td>
                    <td>{new Date(reservation.created_at).toLocaleDateString()}</td>
                    <td>
                      <button 
                        className="btn btn-sm btn-danger glow-btn"
                        onClick={() => handleCancelReservation(reservation.id)}
                      >
                        Cancel
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      <style>
        {`
          .glow-btn {
            box-shadow: 0 0 15px rgba(255, 193, 7, 0.6);
            transition: all 0.3s ease-in-out;
            color: white;
            font-weight: bold;
            border-radius: 8px;
          }

          .glow-btn:hover {
            transform: scale(1.1);
            box-shadow: 0 0 25px rgba(255, 193, 7, 0.9);
            background-color: #218838;
          }

          table th, table td {
            color: #fff;
            font-size: 1.1rem;
          }

          table tbody tr {
            transition: transform 0.2s ease, background-color 0.3s;
          }

          table tbody tr:hover {
            transform: scale(1.05);
            background-color: rgba(255, 255, 255, 0.2);
            box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
          }

          table thead {
            background-color: rgba(0, 0, 0, 0.3);
            color: #fff;
            font-weight: bold;
          }
        `}
      </style>
    </div>
  );
};

export default MyReservations;
