import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../../services/api';

// Sport icons mapped by normalized keys
const sportIcons = {
  football: 'https://cdn-icons-png.flaticon.com/512/861/861512.png',
  basketball: 'https://cdn-icons-png.flaticon.com/512/3449/3449460.png',
  badminton: 'https://cdn-icons-png.flaticon.com/512/2946/2946957.png',
  cricket: 'https://cdn-icons-png.flaticon.com/512/2275/2275931.png',
  volleyball: 'https://cdn-icons-png.flaticon.com/512/3195/3195576.png',
  tennis: 'https://cdn-icons-png.flaticon.com/512/3176/3176294.png',
};

// Helper to get matching icon
const getSportIcon = (type) => {
  if (!type) return sportIcons.football;

  const key = type.toLowerCase().replace(/[^a-z]/g, ''); // normalize
  return sportIcons[key] || sportIcons.football;
};

const FacilityList = () => {
  const [facilities, setFacilities] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFacilities = async () => {
      try {
        const response = await api.get('facilities/');
        setFacilities(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching facilities:', error);
        setLoading(false);
      }
    };

    fetchFacilities();
  }, []);

  if (loading) return <div className="text-center text-white mt-5">Loading...</div>;

  return (
    <div
      style={{
        minHeight: '100vh',
        backgroundImage: `linear-gradient(rgba(0,0,0,0.7), rgba(0,0,0,0.85)), url("https://img.freepik.com/premium-photo/sports-background-advertising-sport-life-concept-generative-ai_1002555-984.jpg?w=2000")`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        padding: '50px 0',
        fontFamily: 'Segoe UI, sans-serif',
      }}
    >
      <div className="container text-white text-center">
        <h1 className="mb-5 display-5 fw-bold text-uppercase" style={{ letterSpacing: '1.5px' }}>
          Explore Our Sports Facilities
        </h1>

        <div className="row justify-content-center">
          {facilities.map((facility) => (
            <div key={facility.id} className="col-lg-4 col-md-6 mb-4 d-flex align-items-stretch">
              <div
                className="card w-100 shadow-lg facility-card"
                style={{
                  borderRadius: '15px',
                  background: 'rgba(255, 255, 255, 0.95)',
                  transition: 'transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
                }}
              >
                <div className="text-center mt-3">
                  <img
                    src={getSportIcon(facility.facility_type)}
                    alt={`${facility.facility_type} icon`}
                    style={{
                      width: '70px',
                      height: '70px',
                      borderRadius: '50%',
                      objectFit: 'cover',
                      border: '3px solid #28a745',
                      backgroundColor: '#fff',
                      padding: '5px',
                      boxShadow: '0 0 15px rgba(40, 167, 69, 0.7)',
                    }}
                  />
                </div>

                {facility.image && (
                  <img
                    src={facility.image}
                    className="card-img-top mt-2"
                    alt={facility.name}
                    style={{
                      height: '180px',
                      objectFit: 'cover',
                      borderRadius: '10px',
                      padding: '0 1rem',
                    }}
                  />
                )}

                <div className="card-body text-dark text-start px-4">
                  <h5 className="card-title fw-bold">{facility.name}</h5>
                  <p className="text-muted mb-1">{facility.facility_type}</p>
                  <p className="card-text text-truncate" title={facility.description}>
                    {facility.description}
                  </p>
                  <Link to={`/facilities/${facility.id}`} className="btn btn-success mt-2 w-100 glow-btn">
                    View Details
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Custom styles */}
      <style>
        {`
          .facility-card:hover {
            transform: scale(1.03);
            box-shadow: 0 0 25px rgba(40, 167, 69, 0.5);
          }

          .glow-btn {
            transition: all 0.3s ease-in-out;
            box-shadow: 0 0 12px rgba(40, 167, 69, 0.6);
          }

          .glow-btn:hover {
            background-color: #218838;
            transform: scale(1.03);
            box-shadow: 0 0 20px rgba(40, 167, 69, 0.9);
          }
        `}
      </style>
    </div>
  );
};

export default FacilityList;
