import React, { useState, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const { login, isAuthenticated } = useContext(AuthContext);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError('Please enter both username and password');
      return;
    }
    const success = await login(username, password);
    if (success) {
      navigate('/facilities');
    } else {
      setError('Invalid credentials. Please try again.');
    }
  };

  if (isAuthenticated) {
    navigate('/facilities');
  }

  const styles = {
    page: {
      display: 'flex',
      height: '100vh',
      backgroundImage: 'url("https://img.freepik.com/premium-photo/sports-background-advertising-sport-life-concept-generative-ai_1002555-984.jpg?w=2000")',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      color: '#fff',
      fontFamily: 'Calibri, sans-serif',
    },
    leftPanel: {
      flex: 1,
      padding: '60px',
      backgroundColor: 'rgba(0, 0, 0, 0.4)', // Lighten the left panel
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
    },
    welcomeText: {
      fontSize: '48px',
      fontWeight: 'bold',
      marginBottom: '20px',
    },
    subText: {
      fontSize: '16px',
      lineHeight: '1.5',
      maxWidth: '80%',
      marginBottom: '20px',
    },
    iconRow: {
      display: 'flex',
      gap: '15px',
      fontSize: '20px',
    },
    rightPanel: {
      flex: 1,
      backgroundColor: 'rgba(255, 255, 255, 0.85)', // Make the right side greyer
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '40px',
    },
    loginBox: {
      width: '100%',
      maxWidth: '400px',
      backgroundColor: 'rgba(255, 255, 255, 0.9)', // Greyish login box
      borderRadius: '10px',
      padding: '20px',
    },
    heading: {
      color: '#111',
      fontSize: '28px',
      marginBottom: '20px',
      textAlign: 'center',
    },
    label: {
      color: '#444',
      marginBottom: '5px',
      display: 'block',
    },
    input: {
      width: '100%',
      padding: '10px',
      marginBottom: '15px',
      border: '1px solid #ccc',
      borderRadius: '4px',
    },
    button: {
      width: '100%',
      padding: '10px',
      backgroundColor: '#E14D2A',
      color: '#fff',
      fontWeight: 'bold',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
    },
    error: {
      color: '#C62828',
      backgroundColor: '#FFEBEE',
      padding: '10px',
      borderRadius: '5px',
      marginBottom: '15px',
      textAlign: 'center',
    },
  };

  return (
    <div style={styles.page}>
      <div style={styles.leftPanel}>
        <div style={styles.welcomeText}>Welcome Back</div>
        <div style={styles.subText}>
          This is a sample authentication page made with React and styled to look like a modern UI login screen.
        </div>
        <div style={styles.iconRow}>
          <i className="fab fa-facebook-f"></i>
          <i className="fab fa-twitter"></i>
          <i className="fab fa-instagram"></i>
          <i className="fab fa-youtube"></i>
        </div>
      </div>

      <div style={styles.rightPanel}>
        <div style={styles.loginBox}>
          <h2 style={styles.heading}>Sign In</h2>
          <form onSubmit={handleLogin}>
            <label style={styles.label} htmlFor="username">Email address</label>
            <input
              type="text"
              id="username"
              style={styles.input}
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter your email"
              required
            />
            <label style={styles.label} htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              style={styles.input}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />
            {error && <div style={styles.error}>{error}</div>}
            <button type="submit" style={styles.button}>Sign in now</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
