// src/components/Common/Navbar.js
import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { FaUserCircle, FaSignOutAlt, FaSignInAlt, FaUserPlus } from 'react-icons/fa';

const Navbar = () => {
  const { isAuthenticated, logout, user } = useContext(AuthContext); // Access user from context
  const navigate = useNavigate();

  const handleLogout = async () => {
    await logout();
    navigate('/login');
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark shadow-sm">
      <div className="container">
        <Link className="navbar-brand fw-bold text-light" to="/">
          <span className="text-primary">College</span> Sports Reservation
        </Link>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto">
            <li className="nav-item">
              <Link className="nav-link active" to="/facilities">Facilities</Link>
            </li>
            {isAuthenticated && (
              <li className="nav-item">
                <Link className="nav-link" to="/my-reservations">My Reservations</Link>
              </li>
            )}
          </ul>
          <div className="d-flex align-items-center">
            {isAuthenticated ? (
              <div className="d-flex align-items-center">
                <span className="text-light me-2">Welcome, {user.username}</span> {/* Displaying the username */}
                <button className="btn btn-outline-light" onClick={handleLogout}>
                  <FaSignOutAlt size={18} className="me-1" /> Logout
                </button>
              </div>
            ) : (
              <>
                <Link className="btn btn-outline-light me-2" to="/login">
                  <FaSignInAlt size={18} className="me-1" /> Login
                </Link>
                <Link className="btn btn-light" to="/register">
                  <FaUserPlus size={18} className="me-1" /> Register
                </Link>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
