// src/components/Facilities/FacilityDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../../services/api';

const FacilityDetail = () => {
  const { facilityId } = useParams();
  const [facility, setFacility] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchFacility = async () => {
      try {
        const response = await api.get(`facilities/${facilityId}/`);
        setFacility(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching facility:', error);
        setError('Failed to load facility details');
        setLoading(false);
      }
    };

    fetchFacility();
  }, [facilityId]);

  if (loading) return <div className="text-center mt-5">Loading...</div>;
  if (error) return <div className="alert alert-danger mt-3">{error}</div>;
  if (!facility) return <div className="alert alert-warning mt-3">Facility not found</div>;

  const imageSrc = facility.image || '/images/default-facility.png';

  return (
    <div
      style={{
        backgroundImage: `linear-gradient(rgba(0,0,0,0.85), rgba(0,0,0,0.85)), url('https://t4.ftcdn.net/jpg/02/81/79/13/360_F_281791395_yMsSMTbxDZ9s7neOIln5yeIyKtSD0Tdo.jpg')`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        color: '#f8f9fa',
        fontFamily: 'Segoe UI, sans-serif',
      }}
    >
      <div className="container py-5">
        {/* Hero Section */}
        <div className="text-center mb-5">
          <h1 className="display-4 fw-bold text-uppercase" style={{ letterSpacing: '2px' }}>
            {facility.name}
          </h1>
          <p className="lead text-light text-opacity-75">Facility Type: {facility.facility_type}</p>
        </div>

        {/* Content Section */}
        <div className="row justify-content-center align-items-start g-4">
          {/* Left Box */}
          <div className="col-md-7">
            <div
              className="card p-4 shadow-lg h-100"
              style={{
                backgroundColor: 'rgba(255, 255, 255, 0.05)',
                border: '1px solid rgba(255, 255, 255, 0.2)',
                backdropFilter: 'blur(10px)',
                borderRadius: '1rem',
                color: '#fff',
              }}
            >
              <h4 className="mb-3">Description</h4>
              <p style={{ color: '#e0e0e0' }}>{facility.description}</p>
              <Link
                to={`/reserve/${facility.id}`}
                className="btn btn-warning btn-lg mt-3 glow-btn"
              >
                Make a Reservation
              </Link>
            </div>
          </div>

          {/* Right Box */}
          <div className="col-md-5">
            <div
              className="image-container mb-4"
              style={{
                overflow: 'hidden',
                borderRadius: '1rem',
                boxShadow: '0 8px 30px rgba(0, 0, 0, 0.5)',
              }}
            >
              <img
                src={imageSrc}
                alt={facility.name}
                className="img-fluid"
                style={{
                  transition: 'transform 0.4s ease',
                }}
                onMouseOver={(e) => (e.currentTarget.style.transform = 'scale(1.05)')}
                onMouseOut={(e) => (e.currentTarget.style.transform = 'scale(1)')}
              />
            </div>

            <div
              className="card text-light p-3 h-100"
              style={{
                backgroundColor: 'rgba(255, 255, 255, 0.05)',
                border: '1px solid rgba(255, 255, 255, 0.2)',
                backdropFilter: 'blur(10px)',
                borderRadius: '1rem',
              }}
            >
              <h5>Additional Information</h5>
              <ul className="list-unstyled mt-3">
                <li><strong>Available for:</strong> Students, Faculty, Staff</li>
                <li><strong>Equipment:</strong> Available on request</li>
                <li><strong>Max duration:</strong> 2 hours per booking</li>
              </ul>
            </div>
          </div>
        </div>
      </div>

      {/* Custom Styles */}
      <style>
        {`
          .glow-btn {
            box-shadow: 0 0 15px rgba(255, 193, 7, 0.6);
            transition: all 0.3s ease-in-out;
          }
          .glow-btn:hover {
            transform: scale(1.05);
            box-shadow: 0 0 25px rgba(255, 193, 7, 0.9);
          }
        `}
      </style>
    </div>
  );
};

export default FacilityDetail;
