import React, { useState, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const { login, isAuthenticated } = useContext(AuthContext);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError('Please enter both username and password');
      return;
    }
    const success = await login(username, password);
    if (success) {
      navigate('/facilities');
    } else {
      setError('Invalid credentials. Please try again.');
    }
  };

  if (isAuthenticated) {
    navigate('/facilities');
  }

  const styles = {
    page: {
      display: 'flex',
      height: '100vh',
      backgroundImage: 'url("https://img.freepik.com/premium-photo/sports-background-advertising-sport-life-concept-generative-ai_1002555-984.jpg?w=2000")',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      color: '#f0f0f0',
      fontFamily: 'Calibri, sans-serif',
    },
    leftPanel: {
      flex: 1,
      padding: '60px',
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
    },
    welcomeText: {
      fontSize: '48px',
      fontWeight: 'bold',
      marginBottom: '20px',
    },
    subText: {
      fontSize: '16px',
      lineHeight: '1.5',
      maxWidth: '80%',
      marginBottom: '20px',
    },
    iconRow: {
      display: 'flex',
      gap: '15px',
      fontSize: '20px',
    },
    rightPanel: {
      flex: 1,
      padding: '60px',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
    },
    form: {
      width: '100%',
      maxWidth: '350px',
      display: 'flex',
      flexDirection: 'column',
      gap: '12px',
    },
    label: {
      color: '#f5f5f5',
      fontSize: '16px',
      marginLeft: '5px',
    },
    input: {
      width: '100%',
      padding: '12px',
      border: '1px solid #aaa',
      borderRadius: '6px',
      backgroundColor: '#f5f5f5',
      color: '#222',
      fontSize: '15px',
      transition: 'box-shadow 0.3s ease-in-out',
    },
    inputFocus: {
      boxShadow: '0 0 6px rgba(0, 0, 0, 0.3)',
    },
    placeholder: {
      color: '#888',
    },
    button: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#001f54',
      color: '#fff',
      fontWeight: 'bold',
      border: 'none',
      borderRadius: '6px',
      cursor: 'pointer',
      fontSize: '16px',
      transition: 'background-color 0.3s ease-in-out',
    },
    buttonHover: {
      backgroundColor: '#00163d',
    },
    error: {
      color: '#880E4F',
      backgroundColor: '#F8BBD0',
      padding: '10px',
      borderRadius: '5px',
      textAlign: 'center',
      fontSize: '14px',
    },
  };

  const [focused, setFocused] = useState({ username: false, password: false });
  const [hovered, setHovered] = useState(false);

  return (
    <div style={styles.page}>
      <div style={styles.leftPanel}>
        <div style={styles.welcomeText}>Welcome to SportSched</div>
        <div style={styles.subText}>
          Book your favorite sports facilities in seconds. Your game, your schedule, one click away.
        </div>
        <div style={styles.iconRow}>
          <i className="fab fa-facebook-f"></i>
          <i className="fab fa-twitter"></i>
          <i className="fab fa-instagram"></i>
          <i className="fab fa-youtube"></i>
        </div>
      </div>

      <div style={styles.rightPanel}>
        <form onSubmit={handleLogin} style={styles.form}>
          <label style={styles.label} htmlFor="username">Email address</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter your email"
            required
            style={{
              ...styles.input,
              ...(focused.username ? styles.inputFocus : {}),
            }}
            onFocus={() => setFocused({ ...focused, username: true })}
            onBlur={() => setFocused({ ...focused, username: false })}
          />

          <label style={styles.label} htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your password"
            required
            style={{
              ...styles.input,
              ...(focused.password ? styles.inputFocus : {}),
            }}
            onFocus={() => setFocused({ ...focused, password: true })}
            onBlur={() => setFocused({ ...focused, password: false })}
          />

          {error && <div style={styles.error}>{error}</div>}

          <button
            type="submit"
            style={{
              ...styles.button,
              ...(hovered ? styles.buttonHover : {}),
            }}
            onMouseEnter={() => setHovered(true)}
            onMouseLeave={() => setHovered(false)}
          >
            Sign in
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
