import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import api from '../../services/api';

const ReservationForm = () => {
  const { facilityId } = useParams();
  const navigate = useNavigate();
  const [facility, setFacility] = useState(null);
  const [date, setDate] = useState(new Date());
  const [timeSlots, setTimeSlots] = useState([]);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchFacility = async () => {
      try {
        const response = await api.get(`facilities/${facilityId}/`);
        setFacility(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching facility:', error);
        setLoading(false);
      }
    };

    fetchFacility();
  }, [facilityId]);

  useEffect(() => {
    const fetchTimeSlots = async () => {
      try {
        const formattedDate = date.toISOString().split('T')[0];
        const response = await api.get(`timeslots/`, {
          params: { facility_id: facilityId, date: formattedDate }
        });
        setTimeSlots(response.data);
      } catch (error) {
        console.error('Error fetching time slots:', error);
      }
    };

    if (facilityId && date) {
      fetchTimeSlots();
    }
  }, [facilityId, date]);

  const handleReservation = async () => {
    if (!selectedTimeSlot) {
      setError('Please select a time slot.');
      return;
    }

    try {
      await api.post('reservations/', {
        time_slot: selectedTimeSlot
      });
      alert('Reservation successful!');
      navigate('/my-reservations');
    } catch (error) {
      console.error('Error creating reservation:', error);
      setError('Failed to create reservation. Please try again.');
    }
  };

  if (loading) return <div className="text-center">Loading...</div>;

  return (
    <div className="container mt-4">
      <h2>Reserve {facility?.name}</h2>
      <div className="card">
        <div className="card-body">
          <div className="form-group mb-3">
            <label>Select Date:</label>
            <DatePicker
              selected={date}
              onChange={date => setDate(date)}
              className="form-control"
              minDate={new Date()}
            />
          </div>

          <div className="form-group mb-3">
            <label>Available Time Slots:</label>
            {timeSlots.length === 0 ? (
              <p>No available time slots for the selected date.</p>
            ) : (
              <div className="list-group">
                {timeSlots.map(slot => (
                  <button
                    key={slot.id}
                    className={`list-group-item list-group-item-action ${selectedTimeSlot === slot.id ? 'active' : ''} ${!slot.is_available ? 'disabled text-muted' : ''}`}
                    onClick={() => slot.is_available && setSelectedTimeSlot(slot.id)}
                    disabled={!slot.is_available}
                  >
                    {new Date(`2000-01-01T${slot.start_time}`).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })} 
                    - 
                    {new Date(`2000-01-01T${slot.end_time}`).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    {!slot.is_available && ' (Already booked)'}
                  </button>
                ))}
              </div>
            )}
          </div>

          {error && <div className="alert alert-danger">{error}</div>}

          <button 
            className="btn btn-primary" 
            onClick={handleReservation}
            disabled={!selectedTimeSlot}
          >
            Make Reservation
          </button>
        </div>
      </div>
    </div>
  );
};

export default ReservationForm;
