import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../../services/api';

const FacilityList = () => {
  const [facilities, setFacilities] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFacilities = async () => {
      try {
        const response = await api.get('facilities/');
        setFacilities(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching facilities:', error);
        setLoading(false);
      }
    };

    fetchFacilities();
  }, []);

  if (loading) return <div className="text-center text-white mt-5">Loading...</div>;

  return (
    <div
      style={{
        minHeight: '100vh',
        backgroundImage: `linear-gradient(rgba(0,0,0,0.85), rgba(0,0,0,0.9)), url("https://img.freepik.com/premium-photo/sports-background-advertising-sport-life-concept-generative-ai_1002555-984.jpg?w=2000")`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        padding: '50px 0',
        fontFamily: 'Segoe UI, sans-serif',
      }}
    >
      <div className="container text-white text-center">
        <h1 className="mb-5 display-5 fw-bold text-uppercase" style={{ letterSpacing: '1.5px' }}>
          Explore Our Sports Facilities
        </h1>

        <div className="row justify-content-center">
          {facilities.map((facility) => (
            <div key={facility.id} className="col-lg-4 col-md-6 mb-5 d-flex align-items-stretch">
              <div
                className="card w-100 shadow facility-card"
                style={{
                  borderRadius: '15px',
                  background: 'rgba(255, 255, 255, 0.96)',
                  transition: 'transform 0.3s ease-in-out',
                  display: 'flex',
                  flexDirection: 'column',
                }}
              >
                {facility.image && (
                  <img
                    src={facility.image}
                    className="card-img-top"
                    alt={facility.name}
                    style={{
                      height: '200px',
                      objectFit: 'cover',
                      borderTopLeftRadius: '15px',
                      borderTopRightRadius: '15px',
                    }}
                  />
                )}

                <div className="card-body text-dark text-start px-4 pb-4">
                  <h5 className="card-title fw-bold">{facility.name}</h5>
                  <p className="text-muted mb-1">{facility.facility_type}</p>
                  <p
                    className="card-text"
                    style={{
                      maxHeight: '3.5em',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis',
                    }}
                    title={facility.description}
                  >
                    {facility.description}
                  </p>
                  <Link
                    to={`/facilities/${facility.id}`}
                    className="btn btn-success mt-3 w-100 glow-btn"
                  >
                    View Details
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Card Hover Effects */}
      <style>
        {`
          .facility-card:hover {
            transform: scale(1.03);
            box-shadow: 0 0 25px rgba(40, 167, 69, 0.4);
          }

          .glow-btn {
            transition: all 0.3s ease-in-out;
            box-shadow: 0 0 12px rgba(40, 167, 69, 0.4);
          }

          .glow-btn:hover {
            background-color: #218838;
            transform: scale(1.02);
            box-shadow: 0 0 18px rgba(40, 167, 69, 0.6);
          }
        `}
      </style>
    </div>
  );
};

export default FacilityList;
