import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../../services/api';
import { FaBasketballBall, FaSwimmer, FaFutbol, FaTableTennis } from 'react-icons/fa';

const FacilityList = () => {
  const [facilities, setFacilities] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFacilities = async () => {
      try {
        const response = await api.get('facilities/');
        setFacilities(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching facilities:', error);
        setLoading(false);
      }
    };

    fetchFacilities();
  }, []);

  const getIcon = (type) => {
    switch (type.toLowerCase()) {
      case 'basketball':
        return <FaBasketballBall size={28} color="#e25822" />;
      case 'football':
        return <FaFutbol size={28} color="#28a745" />;
      case 'swimming':
        return <FaSwimmer size={28} color="#007bff" />;
      case 'tennis':
        return <FaTableTennis size={28} color="#ffc107" />;
      default:
        return <FaFutbol size={28} color="#6c757d" />;
    }
  };

  if (loading) return <div className="text-center text-white mt-5">Loading...</div>;

  return (
    <div
      style={{
        minHeight: '100vh',
        background: `linear-gradient(rgba(0,0,0,0.8), rgba(0,0,0,0.95)), url("https://img.freepik.com/premium-photo/sports-background-advertising-sport-life-concept-generative-ai_1002555-984.jpg?w=2000") no-repeat center center fixed`,
        backgroundSize: 'cover',
        padding: '60px 20px',
        fontFamily: 'Segoe UI, sans-serif',
      }}
    >
      <div className="container text-white text-center">
        <h1
          className="mb-4 fw-bold text-uppercase"
          style={{
            letterSpacing: '2px',
            fontSize: '2.8rem',
            textShadow: '0 0 20px #00ff88',
            animation: 'pulseTitle 2.5s infinite',
          }}
        >
          ⚡ Explore Our Sports Facilities ⚡
        </h1>
        <p className="lead mb-5 text-light" style={{ maxWidth: '700px', margin: '0 auto' }}>
          Dive into the world of sports and discover state-of-the-art facilities that spark your passion and performance.
        </p>

        <div className="row justify-content-center">
          {facilities.map((facility, index) => (
            <div
              key={facility.id}
              className="col-lg-4 col-md-6 mb-5 d-flex align-items-stretch"
              style={{
                animation: `fadeUp 0.8s ease ${index * 0.1}s both`,
              }}
            >
              <div
                className="card facility-card glass-card w-100"
                style={{
                  borderRadius: '18px',
                  background: 'rgba(255, 255, 255, 0.07)',
                  border: '1px solid rgba(255,255,255,0.15)',
                  backdropFilter: 'blur(10px)',
                  WebkitBackdropFilter: 'blur(10px)',
                  boxShadow: '0 8px 24px rgba(0,0,0,0.3)',
                  transition: 'transform 0.4s ease',
                }}
              >
                {facility.image && (
                  <img
                    src={facility.image}
                    className="card-img-top"
                    alt={facility.name}
                    style={{
                      height: '220px',
                      objectFit: 'cover',
                      borderTopLeftRadius: '18px',
                      borderTopRightRadius: '18px',
                    }}
                  />
                )}

                <div className="card-body text-white px-4 pb-4">
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <h5 className="fw-bold mb-0">{facility.name}</h5>
                    <div>{getIcon(facility.facility_type)}</div>
                  </div>
                  <p className="text-muted mb-1 text-capitalize">{facility.facility_type}</p>
                  <p
                    className="card-text"
                    style={{
                      maxHeight: '4.5em',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis',
                      fontSize: '0.95rem',
                    }}
                    title={facility.description}
                  >
                    {facility.description}
                  </p>
                  <Link
                    to={`/facilities/${facility.id}`}
                    className="btn btn-success mt-3 w-100 glow-btn"
                  >
                    View Details
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      <style>
        {`
          @keyframes fadeUp {
            from {
              opacity: 0;
              transform: translateY(30px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }

          @keyframes pulseTitle {
            0%, 100% {
              text-shadow: 0 0 20px #00ff88;
            }
            50% {
              text-shadow: 0 0 40px #00ffaa;
            }
          }

          .facility-card:hover {
            transform: scale(1.04) rotateX(2deg) rotateY(2deg);
            box-shadow: 0 0 25px rgba(40, 167, 69, 0.4);
          }

          .glow-btn {
            transition: all 0.3s ease-in-out;
            box-shadow: 0 0 12px rgba(40, 167, 69, 0.5);
            font-weight: bold;
            border-radius: 50px;
          }

          .glow-btn:hover {
            background-color: #218838;
            transform: scale(1.05);
            box-shadow: 0 0 20px rgba(40, 167, 69, 0.8);
          }
        `}
      </style>
    </div>
  );
};

export default FacilityList;
