import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../../services/api';

const sportIcons = {
  football: 'https://cdn-icons-png.flaticon.com/512/861/861512.png',
  basketball: 'https://cdn-icons-png.flaticon.com/512/3449/3449460.png',
  badminton: 'https://cdn-icons-png.flaticon.com/512/2946/2946957.png',
  cricket: 'https://cdn-icons-png.flaticon.com/512/2275/2275931.png',
};

const FacilityList = () => {
  const [facilities, setFacilities] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFacilities = async () => {
      try {
        const response = await api.get('facilities/');
        setFacilities(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching facilities:', error);
        setLoading(false);
      }
    };

    fetchFacilities();
  }, []);

  if (loading) return <div className="text-center text-white">Loading...</div>;

  return (
    <div
      style={{
        minHeight: '100vh',
        backgroundImage: 'url("https://img.freepik.com/premium-photo/sports-background-advertising-sport-life-concept-generative-ai_1002555-984.jpg?w=2000")',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        paddingTop: '50px',
        paddingBottom: '50px',
      }}
    >
      <div className="container text-white text-center">
        <h2 className="mb-5">Explore Our Sports Facilities</h2>
        <div className="row justify-content-center">
          {facilities.map((facility) => (
            <div key={facility.id} className="col-md-4 mb-4">
              <div
                className="card h-100 shadow-lg"
                style={{
                  borderRadius: '15px',
                  background: 'rgba(255, 255, 255, 0.9)',
                }}
              >
                <div className="text-center mt-3">
                  <img
                    src={
                      sportIcons[
                        facility.facility_type.toLowerCase().replace(' ', '')
                      ] || sportIcons.football
                    }
                    alt="Sport icon"
                    style={{
                      width: '80px',
                      height: '80px',
                      borderRadius: '50%',
                      objectFit: 'cover',
                      border: '3px solid #28a745',
                      backgroundColor: '#fff',
                      padding: '5px',
                    }}
                  />
                </div>
                {facility.image && (
                  <img
                    src={facility.image}
                    className="card-img-top mt-2"
                    alt={facility.name}
                    style={{ height: '180px', objectFit: 'cover', borderRadius: '10px' }}
                  />
                )}
                <div className="card-body text-dark">
                  <h5 className="card-title">{facility.name}</h5>
                  <p className="text-muted">{facility.facility_type}</p>
                  <p className="card-text text-truncate">{facility.description}</p>
                  <Link to={`/facilities/${facility.id}`} className="btn btn-success">
                    View Details
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default FacilityList;
