// src/components/Auth/Login.js
import React, { useState, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';  // Import AuthContext here
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const { login, isAuthenticated } = useContext(AuthContext); // Using the AuthContext
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!username || !password) {
      setError('Please enter both username and password');
      return;
    }

    const success = await login(username, password);
    if (success) {
      navigate('/facilities'); // Redirect to facilities page if login is successful
    } else {
      setError('Invalid credentials. Please try again.');
    }
  };

  if (isAuthenticated) {
    navigate('/facilities'); // Redirect to facilities if already authenticated
  }

  const styles = {
    body: {
      backgroundImage: 'url("https://example.com/sports-background.jpg")', // Sports-themed background (replace with your image)
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundAttachment: 'fixed',
      color: '#F3F4F6', // Off-white text color
      fontFamily: 'Arial, sans-serif',
      margin: 0,
      padding: 0,
      height: '100vh',
    },
    loginContainer: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      backdropFilter: 'blur(5px)', // Blur effect for background
    },
    loginBox: {
      backgroundColor: 'rgba(0, 0, 0, 0.6)', // Black background with transparency
      padding: '30px',
      borderRadius: '8px',
      width: '100%',
      maxWidth: '400px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.5)',
    },
    heading: {
      textAlign: 'center',
      marginBottom: '20px',
      color: '#FFFFFF', // White color for the heading
    },
    formLabel: {
      color: '#B0BEC5', // Light gray for labels
    },
    formControl: {
      backgroundColor: '#2C3E50', // Midnight Blue background for inputs
      color: '#F3F4F6', // Off-white text for inputs
      border: '1px solid #616161', // Gray border
      marginBottom: '15px',
      padding: '10px',
      width: '100%',
    },
    formControlFocus: {
      borderColor: '#FFD700', // Beige border on focus
      boxShadow: '0 0 0 0.2rem rgba(255, 215, 0, 0.25)', // Beige glow on focus
    },
    btnPrimary: {
      backgroundColor: '#FFD700', // Beige button color
      borderColor: '#FFD700',
      width: '100%',
      padding: '10px',
      borderRadius: '5px',
      cursor: 'pointer',
      color: '#FFFFFF', // White text on button
      fontWeight: 'bold',
    },
    btnPrimaryHover: {
      backgroundColor: '#D8B100', // Darker Beige on hover
      borderColor: '#D8B100',
    },
    alert: {
      marginTop: '15px',
      backgroundColor: '#F6D0D0', // Pale pink for error messages
      color: '#721C24', // Dark red text for error
      borderRadius: '5px',
      padding: '10px',
      textAlign: 'center',
    },
  };

  return (
    <div style={styles.loginContainer}>
      <div style={styles.loginBox}>
        <h2 style={styles.heading}>Login</h2>
        <form onSubmit={handleLogin}>
          <div>
            <label htmlFor="username" style={styles.formLabel}>Username</label>
            <input 
              type="text" 
              id="username" 
              style={styles.formControl} 
              value={username} 
              onChange={(e) => setUsername(e.target.value)} 
              required 
            />
          </div>
          <div>
            <label htmlFor="password" style={styles.formLabel}>Password</label>
            <input 
              type="password" 
              id="password" 
              style={styles.formControl} 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              required 
            />
          </div>
          {error && <div style={styles.alert}>{error}</div>}
          <button 
            type="submit" 
            style={styles.btnPrimary} 
            onMouseOver={(e) => e.target.style.backgroundColor = styles.btnPrimaryHover.backgroundColor}
            onMouseOut={(e) => e.target.style.backgroundColor = styles.btnPrimary.backgroundColor}
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
